import React from "react";
import './CardItems.css'
import { useContext } from "react";
import { ShopContext } from "../../Context/ShopContext";
import remve_icon from '../Assets/cart_cross_icon.png'

const CardItems = () => {
  const { all_product, carditems, removeFromCard } = useContext(ShopContext);

  return (
    <div className="carditems">
      <div className="carditems-format-main">
        <p>Product</p>
        <p>Title</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />

      {all_product.map((e) => {
        if (carditems[e.id] > 0) {
          return (
            <div key={e.id}>
              <div className="carditems-format">
                <img src={e.image} alt="" className="carticon-product-icon" />
                <p>{e.name}</p>
                <p>{e.price}</p>

                <button className="cartitem-quantity">{carditems[e.id]}</button>
                <p>{e.new_price * carditems[e.id]}</p>
                <img
                  src={remve_icon}
                  onClick={() => {
                    removeFromCard(e.id);
                  }}
                  alt=""
                />
              </div>
              <hr />
            </div>
          );
        }
        return null; // Added this line to handle the case when the quantity is not greater than 0
      })}
    </div>
  );
}

export default CardItems;
