// ProductDisplay.js
import React from 'react';
import './ProductDisplay.css';
import star_icon from '../Assets/star_icon.png';
import star_dull_icon from '../Assets/star_dull_icon.png';
import data_p from '../Assets/all_product';

const ProductDisplay = ({ item }) => {
  if (!item) {
    // Handle the case when product is undefined
    return null;
  }

  // Find the matching product from the data_p array based on the item ID
  const product = data_p.find((product) => product.id === item.id);

  return (
    <div className='productdisplay'>
      <div className='productdisplay-left'>
        {/* ... your existing code ... */}
      </div>
      <div className='productdisplay-img'>
        <img className='productdisplay-main-img' src={product.image} alt="" />
      </div>
      <div className='productdisplay-right'>
        <h1>{product.name}</h1>
        <div className='productdisplay-right-star'>
          {/* ... your existing code ... */}
        </div>
        <div className='productdisplay-right-prices'>
          {/* ... your existing code ... */}
        </div>
        <div className='productdisplay-right-description'>
          this is a nice production
        </div>
        <div className='productdisplay-right-size'>
          {/* ... your existing code ... */}
        </div>
        <button>ADD TO CARD</button>
        {/* ... your existing code ... */}
      </div>
    </div>
  );
};

export default ProductDisplay;
