import React from "react";
import './CardItems.css'
import { useContext } from "react";
import { ShopContext } from "../../Context/ShopContext";
import remve_icon from '../Assets/cart_cross_icon.png'

const CardItems=()=> {
    const {all_product,CardItems,removeFromCard}=useContext(ShopContext)
  return (
    <div className="carditems" >
     <div className="carditems-format-main">
        <p> Product </p>
        <p> Title </p>
        <p> Price </p>
        <p> Quantity </p>
        <p> Total </p>
        <p> Remove </p>
     </div>
    <hr/>

    {all_product.map((e)=>{
  if(CardItems[e.id]>0) 
  {
 return 
 <div>
 <div className=" carditems-format" >
  <img src="" alt=""  className="carticon-product-icon"/>
  <p> e.name</p>
  <p>e.</p>

  <button className="cartitem-quantity" ></button>
  <p></p>
  <img src={remve_icon} onClick={()=>{removeFromCard()}} alt="" />
 </div>
 <hr/>
</div>
  }
})}

   
    </div>
  );
}

export default CardItems;
