import React from "react";
import './CardItems.css'
import { useContext } from "react";
import { ShopContext } from "../../Context/ShopContext";
import remve_icon from '../Assets/cart_cross_icon.png';

const CardItems = () => {
  const { all_product, cardItems, removeFromCard } = useContext(ShopContext);

  return (
    <div className="carditems">
      <div className="carditems-format-main">
        <p>Product</p>
        <p>Title</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />

      {all_product.map((e) => {
        if (cardItems[e.id] > 0) {
          return (
            <div key={e.id} className="carditems-format">
              <img src={process.env.PUBLIC_URL + e.image} alt={e.name} className="carticon-product-icon" />
              <p>{e.name}</p>
              <p>{e.price}</p>
              <button className="cartitem-quantity">{cardItems[e.id]}</button>
              <p>{e.new_price * cardItems[e.id]}</p>
              <img
                src={remve_icon}
                onClick={() => {
                  removeFromCard(e.id);
                }}
                alt=""
              />
            </div>
          );
        }
        return null;
      })}
    </div>
  );
}

export default CardItems;
