// Breadcrum.js
import React from "react";
import './Breadcrum.css'; // Make sure the path is correct

function Breadcrum({ category, name }) {
  return (
    <div className="breadcrum">
      <p>Home > SHOP {category && `> ${category}`} {name && `> ${name}`}</p>
    </div>
  );
}

export default Breadcrum;
