// Assuming this is inside a component where you are using the Breadcrum component
import React, { useContext } from "react";
import { useParams } from "react-router-dom";
import Breadcrum from "../Components/Breadcrum/Breadcrum";
import { ShopContext } from "../Context/ShopContext";
import arrow_icon from '../Assets/breadcrum_arrow.png'

function YourComponent() {
  const { all_product } = useContext(ShopContext);
  const { productId } = useParams();

  // Find the product based on the productId
  const product = all_product.find((e) => e.id === Number(productId));

  // Check if the product is available
  if (!product) {
    // Optionally, you can render a loading state or handle the absence of the product
    return <p><div className="breadcrum">
    Home <img src={arrow_icon} alt="" /> SHOP <img src={arrow_icon} alt="" />
    {product.category && <span>{product.category}</span>} <img src={arrow_icon} alt="" />
    {product.name && <span>{product.name}</span>}
  </div></p>;
  }

  return (
    <div>
      {/* Other content */}
      <Breadcrum product={product} />
      {/* Other content */}
    </div>
  );
}

export default YourComponent;
