// Breadcrum.jsx
import React from "react";
import { ShopContext } from '../../Context/ShopContext';  // Make sure the import path is correct
import arrow_icon from '../Assets/breadcrum_arrow.png';
import { useParams } from 'react-router-dom';

const Breadcrum = () => {
  const { all_product } = useShopContext();
  const { productId } = useParams();

  // Logic to get the specific product based on your requirements
  const product = all_product.find((e) => e.id === Number(productId));

  return (
    <div className="breadcrum">
      Home <img src={arrow_icon} alt="" /> SHOP <img src={arrow_icon} alt="" />
      {product && product.category && <span>{product.category}</span>} <img src={arrow_icon} alt="" />
      {product && product.name && <span>{product.name}</span>}
    </div>
  );
}

export default Breadcrum;
