// Product.js
import React, { useContext } from "react";
import { ShopContext } from "../Context/ShopContext";
import { useParams } from "react-router-dom";
import Breadcrum from "../Components/Breadcrum/Breadcrum";

function Product({ id, name, image, new_price, old_price }) {
  const { all_product } = useContext(ShopContext);
  const { productId } = useParams(); // Use parentheses to access the property
  const product = all_product.find((e) => e.id === Number(productId));

  return (
    <div key={id}>
      <img src={image} alt={name} />
      <p>{name}</p>
      <p>New Price: ${new_price}</p>
      <p>Old Price: ${old_price}</p>
console.log("Product component - product:", product);
      <Breadcrum product={product} /> {/* Pass the product object instead of productId */}
    </div>
  );
}

export default Product;
